sys_ui_page.client_script:
// Open Lenovo API Retrieval redirect modal redirect modal, and pass url sysparm
jQuery('#redirect').click(function() {

	GlideModal.get().destroy();

	var kb_redirect_modal = new GlideModal('x_500374_leno_warr_kb_redirect_modal'); 	
	kb_redirect_modal.setPreference('sysparm_ui_page', 'lenovo-best-practice-guidance');
	kb_redirect_modal.setWidth(550);
    kb_redirect_modal.render();

});
